import React, { useState } from "react";
import { Link } from "react-router-dom";

export default function AdviceSidebar({ onShareClick, onFilterChange }) {
  const [activeTag, setActiveTag] = useState("Study tips"); // Default active tag

  const handleFilterClick = (tag) => {
    setActiveTag(tag); // Update active tag
    onFilterChange(tag);
  };

  return (
    <aside>
      <h2>Filter</h2>
      <ul>
        {[
          "Study tips",
          "Professors",
          "Housing & Dorm Life",
          "Financial Aid & Scholarships",
          "First-Year Experience",
          "Exam Preparation",
          "Career Advice",
          "Commuter Student Tips",
          "Study Abroad",
          "Alumni Insights",
          "Part-Time Jobs",
          "Internships",
          "Tech & Tools",
          "Off-Campus Life",
          "Networking",
        ].map((tag) => (
          <li key={tag} className={activeTag === tag ? "active" : ""}>
            <Link to="#" onClick={() => handleFilterClick(tag)}>
              {tag}
            </Link>
          </li>
        ))}
      </ul>
      <button className="btn btn-primary btn-block" onClick={onShareClick}>
        Share Advice
      </button>
    </aside>
  );
}
