import React from "react";
import { Link } from "react-router-dom";

export default function AdviceSidebar({
  onShareClick,
  onFilterChange,
  activeTag,
  allPosts, // Add allPosts prop to get all advice posts
}) {
  const tags = [
    "All",
    "Study tips",
    "Professors",
    "Housing & Dorm Life",
    "Financial Aid & Scholarships",
    "First-Year Experience",
    "Exam Preparation",
    "Career Advice",
    "Commuter Student Tips",
    "Study Abroad",
    "Alumni Insights",
    "Part-Time Jobs",
    "Internships",
    "Tech & Tools",
    "Off-Campus Life",
    "Networking",
  ];

  return (
    <aside>
      <h2>Filter</h2>
      <ul>
        {tags.map((tag) => (
          <li key={tag} className={activeTag === tag ? "active" : ""}>
            <Link to="#" onClick={() => handleFilterClick(tag)}>
              {tag}
            </Link>
          </li>
        ))}
      </ul>
      <button className="btn btn-primary btn-block" onClick={onShareClick}>
        Share Advice
      </button>
    </aside>
  );
}
