import React, { useState } from "react";
import Sidebar from "./AdviceSidebar";
import Content from "./AdviceContent";
import AdviceShareModal from "./AdviceShareModal";
import { advicePosts } from "../utils/advicePosts";
import "../styles/Content.css";

export default function Advice() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [filteredPosts, setFilteredPosts] = useState(advicePosts);

  const handleOpenModal = () => setIsModalOpen(true);
  const handleCloseModal = () => setIsModalOpen(false);

  const handleFilterClick = (tag) => {
    setActiveTag(tag); // Update active tag
    onFilterChange(tag);
  };

  return (
    <aside>
      <h2>Filter</h2>
      <ul>
        {[
          "Study tips",
          "Professors",
          "Housing & Dorm Life",
          "Financial Aid & Scholarships",
          "First-Year Experience",
          "Exam Preparation",
          "Career Advice",
          "Commuter Student Tips",
          "Study Abroad",
          "Alumni Insights",
          "Part-Time Jobs",
          "Internships",
          "Tech & Tools",
          "Off-Campus Life",
          "Networking",
        ].map((tag) => (
          <li key={tag} className={activeTag === tag ? "active" : ""}>
            <Link to="#" onClick={() => handleFilterClick(tag)}>
              {tag}
            </Link>
          </li>
        ))}
      </ul>
      <button className="btn btn-primary btn-block" onClick={onShareClick}>
        Share Advice
      </button>
    </aside>
  );
}
