import React, { useState } from "react";
import Sidebar from "./AdviceSidebar";
import Content from "./AdviceContent";
import AdviceShareModal from "./AdviceShareModal";
import { advicePosts } from "../utils/advicePosts";
import "../styles/Content.css";

export default function Advice() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [filteredPosts, setFilteredPosts] = useState(advicePosts);
  const [activeTag, setActiveTag] = useState("All"); // Default active tag
  const [sortCriteria, setSortCriteria] = useState("mostRecent"); // Default sorting criteria

  const handleOpenModal = () => setIsModalOpen(true);
  const handleCloseModal = () => setIsModalOpen(false);

  const handleFilterChange = (tag) => {
    if (tag === "All") {
      setFilteredPosts(advicePosts); // Show all posts when "All" is clicked
    } else {
      const filtered = advicePosts.filter((post) => post.tag === tag);
      setFilteredPosts(filtered);
    }
    setActiveTag(tag); // Update active tag
    sortPosts(sortCriteria); // Sort posts based on current sort criteria
  };

  const handleSortChange = (criteria) => {
    setSortCriteria(criteria);
    let sortedPosts;

    if (criteria === "mostRecent") {
      sortedPosts = [...filteredPosts].sort((a, b) => {
        const timeA = parseTimeAgo(a.timeAgo);
        const timeB = parseTimeAgo(b.timeAgo);
        return timeB - timeA; // Sort in descending order
      });
    } else if (criteria === "mostLiked") {
      sortedPosts = [...filteredPosts].sort((a, b) => b.likes - a.likes);
    } else if (criteria === "mostHearted") {
      sortedPosts = [...filteredPosts].sort((a, b) => b.hearts - a.hearts);
    }

    setFilteredPosts(sortedPosts);
  };

  // Helper function to parse timeAgo string into a timestamp
  const parseTimeAgo = (timeAgo) => {
    const timeParts = timeAgo.split(" ");
    const timeValue = parseInt(timeParts[0], 10);
    const timeUnit = timeParts[1];

    let milliseconds = 0;

    if (timeUnit.startsWith("hr")) {
      milliseconds = timeValue * 60 * 60 * 1000; // Convert hours to milliseconds
    } else if (timeUnit.startsWith("min")) {
      milliseconds = timeValue * 60 * 1000; // Convert minutes to milliseconds
    } else if (timeUnit.startsWith("sec")) {
      milliseconds = timeValue * 1000; // Convert seconds to milliseconds
    }

    return Date.now() - milliseconds; // Return the timestamp for comparison
  };

  return (
    <div className="advice-container">
      <Sidebar
        onShareClick={handleOpenModal}
        onFilterChange={handleFilterChange}
        activeTag={activeTag}
      />
      <Content posts={filteredPosts} onSortChange={handleSortChange} />
      {isModalOpen && <AdviceShareModal onClose={handleCloseModal} />}
    </div>
  );
}
